//import '../index.css';
import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import PopupImage from './PopupImage';

function App() {
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);

    function handleEditProfileClick() {
        setEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setAddPlacePopupOpen(true);
    }

    function handleEditAvatarClick() {
        setEditAvatarPopupOpen(true);
    }

    function closeAllPopups() {
        setEditProfilePopupOpen(false);
        setAddPlacePopupOpen(false);
        setEditAvatarPopupOpen(false);
    }

    // function handleEditAvatarClick() {
    //     const popupAvatar = document.querySelector('#avatar');
    //     popupAvatar.classList.add('popup_is-opened');
    // }

    // function handleEditProfileClick() {
    //     const popupProfile = document.querySelector('#profile');
    //     popupProfile.classList.add('popup_is-opened');
    // }

    // function handleAddPlaceClick() {
    //     const popupPlace = document.querySelector('#add');
    //     popupPlace.classList.add('popup_is-opened');
    // }

  return (
    <body class="page">
        <Header />
        <Main
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            />
        <Footer />
        <PopupImage />
        <PopupWithForm
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            name='profile'
            title='Редактировать профиль'
            button='Сохранить'
            children={
                <>
                    <input
                        type="text"
                        name="name"
                        id="name"
                        class="popup__input popup__input_type_name"
                        placeholder="Имя"
                        minlength="2"
                        maxlength="40"
                        required
                    />
                    <span class="popup__input-error popup__input-error_field_name"></span>
                    <input
                        type="text"
                        name="about"
                        id="about"
                        class="popup__input popup__input_type_about"
                        placeholder="О себе"
                        minlength="2"
                        maxlength="200"
                        required
                    />
                    <span class="popup__input-error popup__input-error_field_about"></span>
                </>
            }
        />
        <PopupWithForm
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            name='add'
            title='Новое место'
            button='Создать'
            children={
                <>
                    <input
                    type="text"
                    name="name" 
                    id="foto" 
                    class="popup__input popup__input_type_name" 
                    placeholder="Название"
                    minlength="2"
                    maxlength="30"
                    required
                    />
                    <span class="popup__input-error popup__input-error_field_place"></span>
                    <input
                    type="url"
                    name="link"
                    id="link"
                    class="popup__input popup__input_type_about"
                    placeholder="Ссылка на картинку"
                    required
                    />
                    <span class="popup__input-error popup__input-error_field_link"></span>
                </>
            }
        />
        <PopupWithForm
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            name='avatar'
            title='Обновить аватар'
            button='Сохранить'
            children={
                <>
                    <input
                    type="url"
                    name="avatar"
                    id="linkAvatar"
                    class="popup__input popup__input_type_about"
                    placeholder="Ссылка на картинку"
                    required
                    />
                    <span class="popup__input-error popup__input-error_field_link"></span>
                </>
            }
        />
        <div id="delete" class="popup">
            <div class="popup__container">
                <h3 class="popup__heading">Вы уверены?</h3>
                <form class="popup__form" name="avatarForm" novalidate>
                    <button name="submit" type="submit" class="popup__submit-button">Да</button>
                </form>
                <button type="button" class="popup__close"></button>
            </div>
        </div>
        <template id="element">
            <li>
                <div class="element">
                    <img class="element__foto" src="https://pictures.s3.yandex.net/frontend-developer/cards-compressed/arkhyz.jpg" alt="фото"/>
                    <div class="element__place">
                        <h2 class="element__place-name"></h2>
                        <div class="element__like-container">
                            <button type="button" class="element__like"></button>
                            <p class="element__amount-likes">0</p>
                        </div>
                    </div>
                    <button type="button" class="element__delete"></button>
                </div>
            </li>
        </template>
    </body>
  );
}

export default App;
