import React from 'react';
import api from '../utils/Api';

function Main(props) {
    const[userName, setUserName] = React.useState('');
    const[userDescription, setUserDescription] = React.useState('');
    const[userAvatar, setUserAvatar] = React.useState('');

    React.useEffect(() => {
        Promise.all([api.getUserInfo(), api.getInitialCards()])
            .then(([user, cards]) => {
                setUserName(user.name);
                setUserDescription(user.about);
                setUserAvatar(user.avatar);
                // userInfo.setUserInfo(user);
                // userId._id = user._id;
                // const reverse = cards.reverse();
                // cardList.renderItems(reverse);
            })
            .catch((err) => {
                console.log(err);
            });
    });

    return (
        <main>
            <section className="profile">
                <div className="profile__container-avatar">
                    <img className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }} alt="аватар" />
                    <div className="profile__avatar-overlay" onClick={props.onEditAvatar}></div>
                </div>
                <div className="profile__info">
                    <div className="profile__name">
                        <h1 className="profile__name-text">{userName}</h1>
                        <button type="button" className="profile__edit-button" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__about">{userDescription}</p>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>
            </section>
            <section className="elements">
                <ul className="elements__list">
                </ul>
            </section>
        </main>
    );
}

export default Main;
