import { useState, useEffect } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import api from '../utils/Api';
import { CurrentUserContext } from '../contexts/CurrentUserContext'
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';

function App() {
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState({isOpen: false});
    const [currentUser, setCurrentUser] = useState({});
    const[cards, setCards] = useState([]);

    useEffect(() => {
        api.getInitialCards()
        .then((cards) => {
            setCards(cards);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    useEffect(() => {
        api.getUserInfo()
        .then((user) => {
            setCurrentUser(user);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        })
        .catch((err) => {
            console.log(err);
        });
    }

    function handleCardDelete(card) {
        api.deleteCard(card._id)
        .then(() => {
            setCards(cards.filter((c) => {
                return c._id !== card._id;
            }))
        })
        .catch((err) => {
            console.log(err);
        });
    }

    function handleEditProfileClick() {
        setEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setAddPlacePopupOpen(true);
    }

    function handleEditAvatarClick() {
        setEditAvatarPopupOpen(true);
    }

    function closeAllPopups() {
        setEditProfilePopupOpen(false);
        setAddPlacePopupOpen(false);
        setEditAvatarPopupOpen(false);
        setSelectedCard({isOpen: false});
    }

    function handleCardClick(card) {
        setSelectedCard({
            isOpen: true,
            link: card.link,
            name: card.name,
        });
    }

    function handleUpdateUser(data) {
        api.setUserInfo(data)
        .then((result) => {
            setCurrentUser(result);
            closeAllPopups();
        })
        .catch((err) => {
            console.log(err);
        });
    }

    function handleUpdateAvatar(data) {
        api.setAvatar(data)
        .then((result) => {
            setCurrentUser(result);
            closeAllPopups();
        })
        .catch((err) => {
            console.log(err);
        });
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
            <div className="page_background">
                <div className="page">
                    <Header />
                    <Main
                        onEditProfile={handleEditProfileClick}
                        onAddPlace={handleAddPlaceClick}
                        onEditAvatar={handleEditAvatarClick}
                        onCardClick={handleCardClick}
                        cards={cards}
                        onCardLike={handleCardLike}
                        onCardDelete={handleCardDelete}
                        />
                    <Footer />
                    <ImagePopup
                        card={selectedCard}
                        onClose={closeAllPopups}
                    />
                    <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
                    <PopupWithForm
                        isOpen={isAddPlacePopupOpen}
                        onClose={closeAllPopups}
                        name='add'
                        title='Новое место'
                        button='Создать'
                    >
                        <input
                            type="text"
                            name="name" 
                            id="foto" 
                            className="popup__input popup__input_type_name" 
                            placeholder="Название"
                            minLength="2"
                            maxLength="30"
                            required
                        />
                        <span className="popup__input-error popup__input-error_field_place"></span>
                        <input
                            type="url"
                            name="link"
                            id="link"
                            className="popup__input popup__input_type_about"
                            placeholder="Ссылка на картинку"
                            required
                        />
                        <span className="popup__input-error popup__input-error_field_link"></span>
                    </PopupWithForm>
                    <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
                    <div id="delete" className="popup">
                        <div className="popup__container">
                            <h3 className="popup__heading">Вы уверены?</h3>
                            <form className="popup__form" name="avatarForm" noValidate>
                                <button name="submit" type="submit" className="popup__submit-button">Да</button>
                            </form>
                            <button type="button" className="popup__close"></button>
                        </div>
                    </div>
                </div>
            </div>
        </CurrentUserContext.Provider>
    );
}

export default App;
