function Main() {
    // const avatar = document.querySelector('.profile__avatar');
    // const buttonEdit = document.querySelector('.profile__edit-button');
    // const buttonAdd = document.querySelector('.profile__add-button');

    // avatar.addEventListener('click', handleEditAvatarClick);
    // buttonEdit.addEventListener('click', handleEditProfileClick);
    // buttonAdd.addEventListener('click', handleAddPlaceClick);

    function handleEditAvatarClick() {
        const popupAvatar = document.querySelector('#avatar');
        popupAvatar.classList.add('popup_is-opened');
    }

    function handleEditProfileClick() {
        const popupProfile = document.querySelector('#profile');
        popupProfile.classList.add('popup_is-opened');
    }

    function handleAddPlaceClick() {
        const popupPlace = document.querySelector('#add');
        popupPlace.classList.add('popup_is-opened');
    }

    return (
        <main>
            <section class="profile">
                <div class="profile__container-avatar">
                    <img class="profile__avatar" src="<%=require('./images/avatar.jpg')%>" alt="аватар"/>
                    <div class="profile__avatar-overlay"></div>
                </div>
                <div class="profile__info">
                    <div class="profile__name">
                        <h1 class="profile__name-text">Жак-Ив Кусто</h1>
                        <button type="button" class="profile__edit-button"></button>
                    </div>
                    <p class="profile__about">Исследователь океана</p>
                </div>
                <button type="button" class="profile__add-button"></button>
            </section>
            <section class="elements">
                <ul class="elements__list">
                </ul>
            </section>
        </main>
    );
}

export default Main;
